/* 使用 React + Ant Design实现一个弹窗组件，组件里面有一个按钮，点击打开弹窗，弹窗内有一个Form表单
点击确认后提交表单，提交表单的值需要当作GroupFilter组件的初始值
弹窗中的：
字段key：下拉框，数据是GROUP_FILTER_DATA
操作符：下拉框，数据是OPERATION_LIST
字段值：输入框，如果操作符为等于，则输入框为必填，如果操作符为属于，则输入框为必填
*/

import { Fragment, useState, useEffect } from 'react';
import { Modal, Form, Input, Button, Select } from 'antd';
import { OPERATION_LIST, GROUP_FILTER_DATA } from '../data';

const CommonModal = ({ setModalValue }) => {
    const [open, setOpen] = useState(false);

    const [form] = Form.useForm();
    const onCancel = () => {
        setOpen(false);
    };  
    const onSubmit = (values) => {
        console.log(values);
        setModalValue?.(values);
        setOpen(false);
    };
    useEffect(() => {
        form?.resetFields();
    }, [open]);
    return (
        <Fragment>
            <Button type="primary" onClick={() => setOpen(true)}>打开弹窗</Button>
            <Modal open={open} onCancel={onCancel} title="这是一个通用的弹窗" footer={null} destroyOnClose>
                <Form form={form} onFinish={onSubmit}>
                    <Form.Item label="字段key" name="field" rules={[{ required: true, message: '请输入字段field' }]}>
                        <Select options={GROUP_FILTER_DATA} showSearch optionFilterProp="label" />
                    </Form.Item>
                    <Form.Item label="操作符" name="operation" rules={[{ required: true, message: '请输入操作符operation' }]}>
                        <Select options={OPERATION_LIST} showSearch optionFilterProp="label" />
                    </Form.Item>
                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type="primary" htmlType="submit" style={{ marginRight: 10 }}>确认</Button>
                        <Button onClick={onCancel}>取消</Button>
                    </Form.Item>
                </Form>
            </Modal>
        </Fragment>
    );
}

export default CommonModal;
