import React, { useState } from 'react';
import { Button, Card, Space, Typography, message } from 'antd';
import GroupFilter from './GroupFilter/index';
const { Title, Paragraph } = Typography;

function App() {
    const [count, setCount] = useState(0);

    const handleIncrement = () => {
        setCount(count + 1);
        message.success(`计数已增加到 ${count + 1}`);
    };

    const handleDecrement = () => {
        setCount(count - 1);
        message.info(`计数已减少到 ${count - 1}`);
    };

    const handleReset = () => {
        setCount(0);
        message.warning('计数已重置');
    };

    return (
        <div style={{ padding: '50px', minHeight: '100vh', background: '#f0f2f5' }}>
            <Card
                style={{
                    maxWidth: '600px',
                    margin: '0 auto',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
                }}
            >
                <Space direction="vertical" size="large" style={{ width: '100%' }}>
                    <Title level={2} style={{ textAlign: 'center' }}>
            React 18 + Antd 4.24.0 Demo
                    </Title>
          
                    <Paragraph style={{ textAlign: 'center', fontSize: '18px' }}>
            当前计数: <strong style={{ fontSize: '24px', color: '#1890ff' }}>{count}</strong>
                    </Paragraph>

                    <Space size="middle" style={{ width: '100%', justifyContent: 'center' }}>
                        <Button type="primary" size="large" onClick={handleIncrement}>
              增加
                        </Button>
                        <Button size="large" onClick={handleDecrement}>
              减少
                        </Button>
                        <Button size="large" onClick={handleReset}>
              重置
                        </Button>
                    </Space>

                    <Paragraph style={{ textAlign: 'center', color: '#8c8c8c', marginTop: '20px' }}>
            这是一个使用 React 18 和 Ant Design 4.24.0 构建的简易示例
                    </Paragraph>
                </Space>
            </Card>
            <GroupFilter />
        </div>
    );
}

export default App;
