// 1 使用 React + Ant Design实现一个弹窗，弹窗内有一个Form表单（可写一个简单的，表单内容随意），点击确认后提交表单

import { Fragment, useState } from 'react';
import { Modal, Form, Input, Button, Select } from 'antd';
import { OPERATION_LIST, GROUP_FILTER_DATA } from '../data';
const CommonModal = () => {
    const [open, setOpen] = useState(false);
    const onCancel = () => {
        setOpen(false);
    };  
    const onSubmit = (values) => {
        console.log(values);
        setOpen(false);
    };
    return (
        <Fragment>
            <Button type="primary" onClick={() => setOpen(true)}>打开弹窗</Button>
            <Modal open={open} onCancel={onCancel} title="这是一个通用的弹窗" footer={null}>
                <Form onFinish={onSubmit}>
                    <Form.Item label="字段key" name="field" rules={[{ required: true, message: '请输入字段field' }]}>
                        <Select options={GROUP_FILTER_DATA} />
                    </Form.Item>
                    <Form.Item label="操作符" name="operation" rules={[{ required: true, message: '请输入操作符operation' }]}>
                        <Select options={OPERATION_LIST} />
                    </Form.Item>
                    <Form.Item label="字段值" name="value" rules={[{ required: true, message: '请输入值value' }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item style={{ textAlign: 'center' }}>
                        <Button type="primary" htmlType="submit" style={{ marginRight: 10 }}>确认</Button>
                        <Button onClick={onCancel}>取消</Button>
                    </Form.Item>
                </Form>
            </Modal>
        </Fragment>
    );
}

export default CommonModal;
