/* 使用 React + Ant Design 实现一个组合筛选的组件
 点击新增筛选条件可以增加一个筛选项，点击右侧删除按钮支持删掉该筛选项
 GROUP_FILTER_DATA: 左侧下拉框的值
 OPERATION_LIST: 中间下拉框的值
 如果中间下拉框的值为等于，则右侧是一个输入框，如果中间下拉框的值为属于，则右侧是一个下拉框，下拉框数据是左侧选择的变量的belongList的值
 组件默认展示形式，见组件默认值.png
 组件示例图，见组件示例图.png
 组件数据图，见组件数据图.png
*/

import { Select, Input } from 'antd';
import {GROUP_FILTER_DATA, OPERATION_LIST} from '../data';
import { useState } from 'react';
import { Button } from 'antd';

const GroupFilter = (initialData = []) => {
    const [dataSource, setDataSource] = useState(initialData || []);

    const dataItemRender = (item, index) => {
        const findItemBelongList = GROUP_FILTER_DATA.find(dataItem => dataItem.value === item.field)?.belongList;
        return (
            <div style={{ display: 'flex', gap: 10, marginBottom: 10 }}>
                <Select
                    options={GROUP_FILTER_DATA} 
                    style={{ width: 200 }} 
                    value={item.field}
                    onChange={(value) => {
                        setDataSource(prev => [
                            ...prev.slice(0, index), 
                            { ...item, field: value }, 
                            ...prev.slice(index + 1)]
                        );
                    }}
                />
                <Select 
                    options={OPERATION_LIST} 
                    style={{ width: 200 }} 
                    value={item.operation}
                    onChange={(value) => {
                        setDataSource(prev => [
                            ...prev.slice(0, index), 
                            { ...item, operation: value, value: undefined }, 
                            ...prev.slice(index + 1)]
                        );
                    }}
                />
                {item.operation === 'equal' ? (
                    <Input 
                        style={{ width: 200 }} 
                        value={item.value}
                        onChange={(e) => {
                            setDataSource(prev => [
                                ...prev.slice(0, index), 
                                { ...item, value: e.target.value }, 
                                ...prev.slice(index + 1)]
                            );
                        }}
                    />
                ) : (
                    <Select 
                        options={findItemBelongList} 
                        style={{ width: 200 }} 
                        value={item.value} 
                        onChange={(value) => {
                            setDataSource(prev => [
                                ...prev.slice(0, index), 
                                { ...item, value }, 
                                ...prev.slice(index + 1)]
                            );
                        }} 
                    />
                )}
                <Button 
                    type="primary"
                    style={{ marginLeft: 10 }}
                    onClick={() => setDataSource(prev => [...prev.slice(0, index), ...prev.slice(index + 1)])}>
                    删除
                </Button>
            </div>
        )
    }
    console.log(dataSource);
    return (
        <div>
            {dataSource?.map((item, index) => dataItemRender(item, index))}
            <Button type="primary" onClick={() => setDataSource(prev => [...prev, { field: undefined, operation: undefined, value: undefined }])}>新增</Button>
        </div>
    )
}

export default GroupFilter;
